// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import SignInPage from './pages/SignInPage';
import BookListPage from './pages/BookListPage';
import BookDetailPage from './pages/BookDetailPage';
import NotFound from './components/NotFound';
import './styles.css';

const App = () => {
  const [user, setUser] = useState(null); // or use localStorage to persist login state
  const [books] = useState([
    { id: '1', name: 'Book 1', summary: 'Summary of Book 1' },
    { id: '2', name: 'Book 2', summary: 'Summary of Book 2' },
    { id: '3', name: 'Book 3', summary: 'Summary of Book 3' },
  ]);

  const handleSignIn = (username) => {
    setUser(username);
  };

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route exact path="/">
            <SignInPage onSignIn={handleSignIn} />
          </Route>
          <Route exact path="/books">
            <BookListPage user={user} books={books} />
          </Route>
          <Route exact path="/books/:id">
            <BookDetailPage user={user} books={books} />
          </Route>
          <Route path="*">
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
