// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import SignInPage from './pages/SignInPage';
import BookListPage from './pages/BookListPage';
import BookDetailPage from './pages/BookDetailPage';
import NotFound from './components/NotFound';
import '../';

const App = () => {
  const [user, setUser] = useState(null); // or use localStorage to persist login state
  const [books] = useState([
    { id: '1', name: 'Book 1', summary: 'Summary of Book 1' },
    { id: '2', name: 'Book 2', summary: 'Summary of Book 2' },
    { id: '3', name: 'Book 3', summary: 'Summary of Book 3' },
  ]);

  const handleSignIn = (username) => {
    setUser(username);
  };

  return (
    <Router>
      <header>
        <h1>Library Management System</h1>
      </header>
      <div className="App">
        <Routes>
          <Route path="/" element={<SignInPage onSignIn={handleSignIn} />} />
          <Route path="/books" element={<BookListPage user={user} books={books} />} />
          <Route path="/books/:id" element={<BookDetailPage user={user} books={books} />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
